import { getHeroesById, getHeroesByOwner } from "./bases/08-impo-expor";


const promesa = new Promise(( resolve,reject ) => {
    setTimeout(() => {
        const heroes = getHeroesById(2);
        resolve(heroes);
    },2000)
});

let heroe;
promesa.then((heroes) => {
    heroe = heroes;
    return heroe;
});

console.log(heroe.owner);
