//funciones

const saludar = function (nombre) {
    return `hola ${nombre}`;    
}

const saludar2 = (nombre) => {
    return `hola ${nombre}`; 
}

const saludar3 = (nombre) => `hola ${nombre}`;

console.log(saludar3("Juan"));

//tarea

function getUsuarioActivo(nombre){
    return {
        id:789876,
        username:nombre,
    }
}

