// console.log(heroe);


const getHeroesByIdAsyn = (id) => {
    return new Promise(( resolve,reject ) => {
        setTimeout(() => {
            const heroes = getHeroesById(id);
            if (resolve) {
                resolve(heroes);
            }else{
                reject(console.warn("error al encontrar el heroe"));
            }
            
        },2000)
    });
};

getHeroesByIdAsyn(3)
.then((heroe) => {
    console.log("El heroe es " , heroe)
    .catch();
});
