const persona = {
    name:"tonny",
    surname: "burcher",
    age:45,
    direccion:{
        ciudad:"new york",
        zip:1456464564434768,
        lat:14.565762,
        long:45.876765,

    }
};

//podemos querer clonar persona uno en otra de la siguiente manera

const persona2 = persona;

//pero esto realmente lo que hace es compartir ambos el mismo espacio en memoria 
//de hecho si cambiamos persona cambiariamos persona2

persona2.nombre="manolo";
console.log(persona);
console.table(persona);
