export const useFetchGifs = () => {
    
    const [state, setState] = useState({
        data:[],
        loading:true
    });

    setTimeout(() => {
        setState({
            data:[],
            loading:true
        })
    }, 3000);
    return state;// el state aqui es la data {data:[], y el estado que es true loading:true}
}
