import React, { useEffect, useState } from 'react'
import { getGift } from '../helpers/getGifts';
import { GifGridItem } from './GifGridItem';


export const GifGrid = ({category}) => {

    const [images, setImages] = useState([]);
   
    

    useEffect(()=>{
        getGift(category);
    }, [])

    
    //getGift();

  return (
    <>
    <h3>{category}</h3>
    <div className='card-grid'>
    
       {
           images.map( img  => (
             <GifGridItem 
             key={img.id}
             {...img}
             />
           ))
           
       }
    
    </div>
    </>
  )
}
