//   }

    return (
        <>
        <p>GifExpertApp</p>
        <h2>GifExpertApp</h2>
        <AddCategory setCategories={ setCategories }/>
        <hr />
     

        <ol>
            {
                categories.map(category => 
                     <li key={ category }> { category }</li>
                )
            }
        </ol>
        </>

    );

}
