import { getGift } from "../helpers/getGifts";



export const useFetchGifs = (category) => {
    
    const [state, setState] = useState({
        data:[],
        loading:true
    });

    useEffect(()=>{

        getGift(category)
        .then( imgs => {

            setTimeout(() => {

            })
            setState({
                data:imgs,
                loading:false
            });
        },3000 )
    },[category])

    setTimeout(() => {
        setState({
            data:[2,3,4,5,6,7,8,9,],
            loading:false
        })
    }, 3000);
    return state;// el state aqui es la data {data:[], y el estado que es true loading:true}
}
