import React from 'react';
import { useForm } from '../../../hook/useForm';
import "./effects.css";


export const FormWithCustomHooks = () => {


   //creamos un formulario con los objetos nombre y email
   const [FormValues, handleInputChang] = useForm({

    name:"",
    email:"",
    password:"",
  });

  //desestructuramos el nombre y el email
  const {name,email,password} = FormValues;

  const handleInputChange = ({target}) =>{
    //vamos a hacer
    setFormState({
      //llamamos a todo el formulario por si hay propiedades que no cambian
      ...formState,
      //setFormState aqui tenemos lo que se escriba en el objeto input
      [target.name]: target.value
       
    });
  };


  return (
    <>
    <h1>FormWithCustomHooks</h1>
    <hr />
    <div className='form-group'>
      <input 
      type="text"
      name='name'
      className='form-control'
      placeholder='Introduce tu nombre'
      autoComplete='off'
      value={name}
      //se llama cada vez que se produce un cambio 
      onChange={ handleInputChange }
      />

    </div>


    <div className='form-group'>
      <input 
      type="text"
      name='email'
      className='form-control'
      placeholder='Introduce tu email'
      autoComplete='off'
      value={email}
      //se llama cada vez que se produce un cambio 
      onChange={ handleInputChange }
      />

    </div>
   
    <div className='form-group'>
      <input 
      type="password"
      name='password'
      className='form-control'
      placeholder='Introduce tu password'
      value={password}
      //se llama cada vez que se produce un cambio 
      onChange={ handleInputChange }
      />

    </div>
    </>
  )
}
