    const [formState, setformState] = useState({

      name:"",
      email:"",
    });
    //desestructuramos el nombre y el email
    const {name,email} = formState;
    console.log("prueba");
  })
  return (
    <>
    <h1>useEffect</h1>
    <hr />
    <div className='form-group'>
      <input 
      type="text"
      name='name'
      className='form-control'
      placeholder='Introduce tu nombre'
      autoComplete='off'/>

    </div>
    </>
  )
}
