import React, { useContext } from 'react'
import { UserContext } from './UserContext'

export const LoginScreen = () => {
  
  //vamos a extraer el setUser del userContext
  const {setUser} = useContext(UserContext)
  return (
    <div>
        <h1>LoginScreen</h1>
        <hr />
        <button
        className='btn btn-primary'
        onClick={ ()=> setUser({
          id:123,
          name:"Juan"
        })}>Login</button>
    </div>
  )
}
