import React from 'react';
import { useCounter } from '../../hook/useCounter';
import "./counter.css";

export const CounterWithCustomHook = () => {

    useCounter
  return (
    <>
      <h1>Counter with hook: { 0 }</h1> 
      <hr /> 
      <button className='btn'>+1</button>
      <button className='btn'>-1</button>
    </>
  )
}
